    "plt.plot(x, rho, label=r\"exact solution\")\n",
    "plt.plot(x_pos, rho_final, color = 'red', label = r\"numerical approximation\")\n",
    "plt.xlabel(\"x\")\n",
    "plt.ylabel(r\"$\\Sigma$\")\n",
    "#plt.title(\"Exact Riemann Solution\")\n",
